INSERT INTO `sc_questions` (`id`, `orders`, `quetype`, `questions`, `quecont`, `images`, `options`, `answer`, `note`, `red`, `class`, `classification`) VALUES
(80, 12, 1, 1, '路面上的黄色标记是何含义？', '/Public/Uploads/2017-04-21/1492763637.jpg', 'a:4:{s:1:"A";s:12:"禁止掉头";s:1:"B";s:12:"禁止转弯";s:1:"C";s:12:"禁止直行";s:1:"D";s:12:"允许掉头";}', 'A', '图中的X就是禁止的意思，旁边一个掉头，合起来就是禁止掉头。', '', 1, 101),
(81, 13, 1, 1, '路面上的黄色标记是何含义？', '/Public/Uploads/2017-04-21/1492763728.jpg', 'a:4:{s:1:"A";s:12:"禁止直行";s:1:"B";s:12:"禁止左转";s:1:"C";s:12:"禁止右转";s:1:"D";s:12:"禁止掉头";}', 'C', '图中的X是禁止的意思，旁边是右转弯，合起来就是禁止右转。', '', 1, 101),
(72, 6, 1, 1, '路中心的双黄实线属于哪一类标线？', '/Public/Uploads/2017-04-21/1492762096.jpg', 'a:4:{s:1:"A";s:12:"警告标志";s:1:"B";s:12:"指示标线";s:1:"C";s:12:"禁止标线";s:1:"D";s:12:"辅助标线";}', 'C', '虚线是指示标线，本题是实线，答案选禁止标线。', '实线,禁止', 1, 101),
(73, 7, 1, 1, '路中心双黄实线是何含义？', '/Public/Uploads/2017-04-21/1492762200.jpg', 'a:4:{s:1:"A";s:36:"双侧可跨越同向车道分界线";s:1:"B";s:27:"单向行驶车道分界线";s:1:"C";s:36:"禁止跨越对向车行道分界线";s:1:"D";s:30:"可跨越对向车道分界线";}', 'C', '实线是禁止跨越的，答案选择禁止跨越对向车行道分界线。', '实线 , 禁止', 1, 101),
(74, 8, 1, 1, ' 路中心的黄色斜线填充是何含义？', '/Public/Uploads/2017-04-21/1492762302.jpg', 'a:4:{s:1:"A";s:30:"可跨越对向车道分界线";s:1:"B";s:36:"双侧可跨越同向车道分界线";s:1:"C";s:27:"单向行驶车道分界线";s:1:"D";s:36:"禁止跨越对向车行道分界线";}', 'D', '这种黄色斜线也是实线，实线都是禁止跨越的,答案选择禁止跨越对向车行道分界线。', '禁止', 1, 101),
(75, 9, 1, 1, '路中心白色实线是何含义？', '/Public/Uploads/2017-04-21/1492762393.jpg', 'a:4:{s:1:"A";s:36:"单侧可跨越同向车道分界线";s:1:"B";s:36:"禁止跨越对向车行道分界线";s:1:"C";s:36:"禁止跨越同向车行道分界线";s:1:"D";s:36:"双侧可跨越同向车道分界线";}', 'C', '图中是实线，箭头方向是同向，答案选择禁止跨越同向车行道分界线。', '', 1, 101),
(76, 10, 1, 1, '路两侧的车行道边缘白色实线是什么含义？', '/Public/Uploads/2017-04-21/1492762466.jpg', 'a:4:{s:1:"A";s:27:"非机动车可临时跨越";s:1:"B";s:18:"禁止车辆跨越";s:1:"C";s:24:"机动车可临时跨越";s:1:"D";s:21:"车辆可临时跨越";}', 'B', '图中红圈内是实线，实线都不能跨越，答案选择禁止车辆跨越', '', 1, 101),
(78, 11, 1, 1, '路中心黄色虚实线是何含义？', '/Public/Uploads/2017-04-21/1492763453.jpg', 'a:4:{s:1:"A";s:24:"虚线一侧禁止越线";s:1:"B";s:24:"两侧均可越线行驶";s:1:"C";s:24:"实线一侧允许越线";s:1:"D";s:24:"实线一侧禁止越线";}', 'D', '我们始终记住虚线可以越线，实线禁止越线，所以答案中只有选择实线一侧禁止越线。', '', 1, 101),
(69, 4, 1, 1, '路中白色 虚线 是什么标线？', '/Public/Uploads/2017-04-21/1492761761.jpg', 'a:4:{s:1:"A";s:33:"限制跨越对向车道中心线";s:1:"B";s:33:"禁止跨越对向车道中心线";s:1:"C";s:30:"可跨越同向车道中心线";s:1:"D";s:33:"单向行驶车道分界中心线";}', 'C', '虚线都是可以跨越的，图中箭头是指向同一个方向，简称同向，答案选择可跨越同向车道中心线。', '虚线,可', 1, 101),
(64, 1, 1, 1, '路中心黄色虚线属于哪一类标线？', '/Public/Uploads/2017-04-21/1492761198.jpg', 'a:4:{s:1:"A";s:12:"指示标线";s:1:"B";s:12:"辅助标线";s:1:"C";s:12:"警告标志";s:1:"D";s:12:"禁止标线";}', 'A', '在标线中我们考试只涉及到两类，指示标线和禁止标线，虚线是指示标线，实线是禁止标线。本题答案选择指示标线。', '指示', 1, 101),
(67, 3, 1, 1, '路中黄色分界线的作用是什么？', '/Public/Uploads/2017-04-21/1492761421.jpg', 'a:4:{s:1:"A";s:27:"禁止跨越对向行车道";s:1:"B";s:27:"允许在左侧车道行驶";s:1:"C";s:30:"分隔同向行驶的交通流";s:1:"D";s:30:"分隔对向行驶的交通流";}', 'D', '黄色分界线就是起分隔作用，看图中的两个箭头是相对的方向，简称对向，答案选择分隔对向行驶的交通流', '分,对向 ', 1, 101),
(77, 2, 1, 1, '指示标线的作用是什么？', '', 'a:4:{s:1:"A";s:12:"禁止通行";s:1:"B";s:12:"警告提醒";s:1:"C";s:12:"限制通行";s:1:"D";s:12:"指示通行";}', 'D', '指示标线当然是起指示作用，答案选指示通行。', '指示', 1, 101),
(71, 5, 1, 1, '路右侧车行道边缘白色 虚线 是什么含义？', '/Public/Uploads/2017-04-21/1492761963.jpg', 'a:4:{s:1:"A";s:21:"人行横道分界线";s:1:"B";s:27:"车辆可临时越线行驶";s:1:"C";s:21:"应急车道分界线";s:1:"D";s:24:"车辆禁止越线行驶";}', 'B', '虚线都是可以跨越的，答案选车辆可临时越线行驶。', '虚线,可 ', 1, 101),
(82, 14, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492763823.jpg', 'a:4:{s:1:"A";s:12:"指示禁行";s:1:"B";s:12:"指示合流";s:1:"C";s:12:"指示直行";s:1:"D";s:12:"指示车道";}', 'C', '图中一个箭头向上，表示直行，答案选择指示直行。', '', 1, 101),
(83, 15, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492763911.jpg', 'a:4:{s:1:"A";s:21:"指示直行或掉头";s:1:"B";s:27:"指示向左转弯或掉头";s:1:"C";s:27:"指示直行或向左变道";s:1:"D";s:24:"指示直行或左转弯";}', 'D', '图中两个箭头方向要看准，一个直行和一个左转，答案选择指示直行或左转弯，记住是转弯，没有变道的标线。', '', 1, 101),
(84, 16, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492763999.jpg', 'a:4:{s:1:"A";s:27:"指示向右转弯或掉头";s:1:"B";s:21:"指示直行或掉头";s:1:"C";s:27:"指示直行或向右变道";s:1:"D";s:24:"指示直行或右转弯";}', 'D', '图中两个箭头方向要看准，一个直行和一个右转，答案选择指示直行或右转弯，记住是转弯，没有变道的标线。', '', 1, 101),
(85, 17, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764083.jpg', 'a:4:{s:1:"A";s:21:"指示前方左转弯";s:1:"B";s:18:"指示前方直行";s:1:"C";s:21:"指示前方右转弯";s:1:"D";s:18:"指示向左变道";}', 'A', '图中是向左的转弯，答案选择指示前方左转弯。', '', 1, 101),
(86, 18, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764171.jpg', 'a:4:{s:1:"A";s:18:"指示前方掉头";s:1:"B";s:21:"指示前方右转弯";s:1:"C";s:18:"指示前方直行";s:1:"D";s:18:"指示向左变道";}', 'B', '图中是向右的转弯，答案选择指示前方右转弯', '', 1, 101),
(87, 19, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764285.jpg', 'a:4:{s:1:"A";s:18:"指示前方右转";s:1:"B";s:18:"指示前方直行";s:1:"C";s:18:"指示向左变道";s:1:"D";s:18:"指示前方掉头";}', 'D', '图中是掉头，答案选择指示前方掉头。', '', 1, 101),
(88, 20, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764369.jpg', 'a:4:{s:1:"A";s:30:"指示前方直行向左变道";s:1:"B";s:30:"指示前方可直行或掉头";s:1:"C";s:30:"指示前方可左转或掉头";s:1:"D";s:30:"指示前方可直行或左转";}', 'B', '图中两个箭头，直行和掉头，答案选择指示前方可直行或掉头。', '', 1, 101),
(89, 21, 1, 1, '这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764520.jpg', 'a:4:{s:1:"A";s:30:"指示前方可左转或掉头";s:1:"B";s:30:"指示前方可直行或掉头";s:1:"C";s:30:"指示前方可直行或左转";s:1:"D";s:36:"指示前方可直行或向左变道";}', 'A', '图中两个箭头方向指向左侧和掉头，答案选择指示前方可左转或掉头', '', 1, 101),
(90, 22, 1, 1, '路面上导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764614.jpg', 'a:4:{s:1:"A";s:36:"指示前方道路是分离式道路";s:1:"B";s:36:"指示前方道路需向左右合流";s:1:"C";s:34:"指示前方道路是Y型交叉口";s:1:"D";s:36:"指示前方道路仅可左右转弯";}', 'D', '图中两个箭头其实就是一个左转弯加一个右转弯，合起来就是左右转弯，答案选择指示前方道路仅可左右转弯。', '', 1, 101),
(91, 23, 1, 1, ' 这个导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764707.jpg', 'a:4:{s:1:"A";s:42:"提示前方右侧有障碍需向左合流";s:1:"B";s:39:"提示前方有左弯或需向左合流";s:1:"C";s:39:"提示前方有右弯或需向右合流";s:1:"D";s:39:"提示前方有左弯或许向左绕行";}', 'B', '图中箭头是弧形的，表示要向左小转弯，或者车道数减少需要合流，答案选择提示前方有左弯或需向左合流', '', 1, 101),
(92, 24, 1, 1, '路面上导向箭头是何含义？', '/Public/Uploads/2017-04-21/1492764787.jpg', 'a:4:{s:1:"A";s:39:"提示前方有右弯或需向右合流";s:1:"B";s:39:"提示前方有左弯或需向左绕行";s:1:"C";s:39:"提示前方有左弯或需向左合流";s:1:"D";s:36:"提示前方有障碍需向左合流";}', 'A', '图中箭头是弧形的，表示要向右小转弯，或者车道数减少需要合流，答案选择提示前方有右弯或需向右合流', '', 1, 101),
(93, 25, 1, 1, '图中圈内白色实线是什么标线？', '/Public/Uploads/2017-04-21/1492764919.jpg', 'a:4:{s:1:"A";s:21:"可变导向车道线";s:1:"B";s:15:"方向引导线";s:1:"C";s:15:"导向车道线";s:1:"D";s:15:"单向行驶线";}', 'C', '图中红圈内是固定了行驶方向的车道线，答案选择导向车道线。', '', 1, 101),
(94, 26, 1, 1, '图中圈内的锯齿状白色实线是什么标线？', '/Public/Uploads/2017-04-21/1492764990.jpg', 'a:4:{s:1:"A";s:15:"导向车道线";s:1:"B";s:21:"可变导向车道线";s:1:"C";s:15:"方向引导线";s:1:"D";s:15:"单向行驶线";}', 'B', '图中红圈内也是车道线，但是没有固定行驶方向，在这条道的车辆应该根据信号灯或指示牌的方向行驶，答案选择可变导向车道线。', '', 1, 101),
(95, 27, 1, 1, '图中圈内两条白色虚线是什么标线？', '/Public/Uploads/2017-04-21/1492765073.jpg', 'a:4:{s:1:"A";s:18:"小型车转弯线";s:1:"B";s:18:"交叉路停车线";s:1:"C";s:15:"掉头引导线";s:1:"D";s:18:"左弯待转区线";}', 'D', '图中红圈内的区域设置在左转弯车道前方，在直行灯为绿灯的时候，左转弯车辆可以进入这块区域等待左转，这就是左弯待转区线。', '', 1, 101),
(96, 28, 1, 1, '图中圈内三角填充区域是什么标线？', '/Public/Uploads/2017-04-21/1492765146.jpg', 'a:4:{s:1:"A";s:9:"导流线";s:1:"B";s:9:"减速线";s:1:"C";s:9:"网状线";s:1:"D";s:9:"停车线";}', 'A', '红圈内标线用于路口引导车流行驶，车辆不得压线或越线行驶，答案是导流线。', '', 1, 101),
(97, 29, 1, 1, '图中圈内的路面标记是什么标线？', '/Public/Uploads/2017-04-21/1492765231.jpg', 'a:4:{s:1:"A";s:15:"人行横道线";s:1:"B";s:15:"减速让行线";s:1:"C";s:15:"停车让行线";s:1:"D";s:15:"路口示意线";}', 'A', '路口横过马路的白色斑马线就是人行横道线。', '', 1, 101),
(98, 30, 1, 1, '这个地面标记是什么标线？', '/Public/Uploads/2017-04-21/1492765320.jpg', 'a:4:{s:1:"A";s:18:"减速让行预告";s:1:"B";s:18:"停车让行预告";s:1:"C";s:18:"交叉路口预告";s:1:"D";s:18:"人行横道预告";}', 'D', '这个标记一般设置在将要到达人行横道前30-50米处，提示车辆驾驶人，前方即将通过人行横道，应当减速慢行，并注意横过道路的行人。', '', 1, 101),
(99, 31, 1, 1, '图中圈内白色虚线是什么标线？', '/Public/Uploads/2017-04-21/1492766045.jpg', 'a:4:{s:1:"A";s:18:"小型车转弯线";s:1:"B";s:21:"非机动车引导线";s:1:"C";s:15:"路口导向线";s:1:"D";s:15:"车道连接线";}', 'C', '路口内的虚线不分颜色都是路口导向线。', '', 1, 101),
(100, 32, 1, 1, '图中圈内黄色虚线是什么标线？', '/Public/Uploads/2017-04-21/1492766114.jpg', 'a:4:{s:1:"A";s:18:"小型车转弯线";s:1:"B";s:21:"非机动车引导线";s:1:"C";s:15:"车道连接线";s:1:"D";s:15:"路口导向线";}', 'D', '路口内的虚线不分颜色都是路口导向线。', '', 1, 101),
(101, 33, 1, 1, '路面由白色虚线和三角地带标线组成的是什么标线？', '/Public/Uploads/2017-04-21/1492766182.jpg', 'a:4:{s:1:"A";s:18:"道路入口标线";s:1:"B";s:21:"可跨越式分道线";s:1:"C";s:18:"道路出口标线";s:1:"D";s:21:"道路出口减速线";}', 'C', '图中是道路的出口，出口处的标线就是出口标线。', '', 1, 101),
(122, 51, 1, 1, '路口最前端的双白虚线是什么含义？', '/Public/Uploads/2017-04-21/1492776649.jpg', 'a:4:{s:1:"A";s:15:"减速让行线";s:1:"B";s:15:"左弯待转线";s:1:"C";s:15:"停车让行线";s:1:"D";s:15:"等候放行线";}', 'A', '图中有减速让行标志，表示这个路口要减速让行，双白色虚线就是减速让行线', '', 1, 101),
(103, 34, 1, 1, '路面上白色虚线和三角地带标线组成的是什么标线？', '/Public/Uploads/2017-04-21/1492767138.jpg', 'a:4:{s:1:"A";s:18:"道路出口标线";s:1:"B";s:18:"道路入口标线";s:1:"C";s:21:"道路入口减速线";s:1:"D";s:21:"可跨越式分道线";}', 'B', '图中是道路的入口，入口处的标线就是入口标线。', '', 1, 101),
(104, 35, 1, 1, '路中两条双黄色虚线是什么标线？', '/Public/Uploads/2017-04-21/1492767747.jpg', 'a:4:{s:1:"A";s:15:"潮汐车道线";s:1:"B";s:18:"可跨越分道线";s:1:"C";s:15:"单向分道线";s:1:"D";s:15:"双向分道线";}', 'A', '两条双黄虚线内是一条车道，这是潮汐车道线，根据早晚交通流量不同的情况，通过车道信号灯控制潮汐车道的行驶方向，就像大海涨潮退潮一样。', '', 1, 101),
(105, 36, 1, 1, '图中圈内的白色折线是什么标线？', '/Public/Uploads/2017-04-21/1492767822.jpg', 'a:4:{s:1:"A";s:15:"车距确认线";s:1:"B";s:15:"路口减速线";s:1:"C";s:15:"车速确认线";s:1:"D";s:15:"减速行驶线";}', 'A', '这是车距确认线，红圈内的白色折线的间距为50米，帮助在高速行驶的驾驶人确认与前车之间的纵向距离。', '', 1, 101),
(2585, 37, 1, 1, '图中圈内的白色半圆状标记是什么标线？', '/Public/Uploads/2017-04-27/1493283492.jpg', 'a:4:{s:1:"A";s:15:"车距确认线";s:1:"B";s:15:"减速行驶线";s:1:"C";s:15:"路口减速线";s:1:"D";s:15:"车速确认线";}', 'A', '图中的白色半圆在车辆驶过时会与轮胎摩擦发出尖锐的声音，提醒车辆注意与路肩的横向距离，因此答案是车距确认线。', '', 1, 101),
(108, 38, 1, 1, '这个路面数字标记是何含义？', '/Public/Uploads/2017-04-21/1492768081.jpg', 'a:4:{s:1:"A";s:18:"道路编号标记";s:1:"B";s:18:"最小间距标记";s:1:"C";s:18:"速度限制标记";s:1:"D";s:18:"保持车距标记";}', 'C', '图中的数字是路面上的速度限制标记，提醒车辆驾驶人按照标记标明的速度行驶。', '', 1, 101),
(109, 39, 1, 1, '这个路面标记是何含义？', '/Public/Uploads/2017-04-21/1492768173.jpg', 'a:4:{s:1:"A";s:31:"平均速度为100公里/小时";s:1:"B";s:31:"最低限速为100公里/小时";s:1:"C";s:28:"解除100公里/小时限速";s:1:"D";s:31:"最高限速为100公里/小时";}', 'D', '路面的黄色速度标记表示最高限速，答案选最高限速为100公里/小时', '', 1, 101),
(110, 40, 1, 1, '这个路面标记是何含义？', '/Public/Uploads/2017-04-21/1492768249.jpg', 'a:4:{s:1:"A";s:30:"最低限速为80公里/小时";s:1:"B";s:30:"最高限速为80公里/小时";s:1:"C";s:27:"解除80公里/小时限速";s:1:"D";s:30:"平均速度为80公里/小时";}', 'A', '路面白色标记表示最低限速，答案选择最低限速为80公里/小时', '', 1, 101),
(111, 41, 1, 1, '这个路面标记是何含义？', '/Public/Uploads/2017-04-21/1492768324.jpg', 'a:4:{s:1:"A";s:18:"摩托车专用道";s:1:"B";s:18:"电瓶车专用道";s:1:"C";s:15:"非机动车道";s:1:"D";s:18:"自行车专用道";}', 'C', '标记是非机动车，表示车道为非机动车道', '', 1, 101),
(112, 42, 1, 1, '这种白色矩形标线框含义是什么？', '/Public/Uploads/2017-04-21/1492768395.jpg', 'a:4:{s:1:"A";s:18:"倾斜式停车位";s:1:"B";s:33:"出租车专用上下客停车位";s:1:"C";s:18:"垂直式停车位";s:1:"D";s:18:"平行式停车位";}', 'D', '路边的标线框是车位，这些车位与路面是平行的，答案选择平行式停车位。', '', 1, 101),
(113, 43, 1, 1, '这种停车标线含义是什么？', '/Public/Uploads/2017-04-21/1492768466.jpg', 'a:4:{s:1:"A";s:27:"固定停车方向停车位";s:1:"B";s:24:"专用上下客停车位";s:1:"C";s:21:"专用待客停车位";s:1:"D";s:24:"机动车限时停车位";}', 'A', '图中的每个车位内都有箭头标明了方向，表示车辆只能按照固定的方向停车，答案选固定停车方向停车位。', '', 1, 101),
(114, 44, 1, 1, '这种白色矩形标线框含义是什么？', '/Public/Uploads/2017-04-21/1492768536.jpg', 'a:4:{s:1:"A";s:15:"专用停车位";s:1:"B";s:15:"限时停车位";s:1:"C";s:15:"免费停车位";s:1:"D";s:15:"长时停车位";}', 'B', '图中的车位上方都标明了时间段，19点到6点，说明只能在这段时间内停车，答案选限时停车位', '', 1, 101),
(115, 45, 1, 1, '红色圆圈内标线含义是什么？', '/Public/Uploads/2017-04-21/1492768621.jpg', 'a:4:{s:1:"A";s:15:"应急停车带";s:1:"B";s:15:"临时停靠站";s:1:"C";s:18:"公交车停靠站";s:1:"D";s:18:"港湾式停靠站";}', 'D', '红圈内的实线和路沿组成一个港湾一样的区域，答案选择港湾式停靠站。', '', 1, 101),
(116, 46, 1, 1, '红色圆圈内标线含义是什么？', '/Public/Uploads/2017-04-21/1492768712.jpg', 'a:4:{s:1:"A";s:15:"应急停车带";s:1:"B";s:18:"公交车停靠站";s:1:"C";s:18:"大客车停靠站";s:1:"D";s:15:"临时停靠站";}', 'B', '图中写有公交车三个字，答案选公交车停靠站。', '', 1, 101),
(117, 47, 1, 1, '路缘石上的黄色虚线是何含义？', '/Public/Uploads/2017-04-21/1492768788.jpg', 'a:4:{s:1:"A";s:18:"禁止临时停车";s:1:"B";s:18:"禁止装卸货物";s:1:"C";s:18:"禁止长时停车";s:1:"D";s:18:"禁止上下人员";}', 'C', '路沿石上的线是黄色虚线的，表示禁止长时停车。', '', 1, 101),
(119, 48, 1, 1, '路缘石上的黄色实线是何含义？', '/Public/Uploads/2017-04-21/1492768938.jpg', 'a:4:{s:1:"A";s:21:"禁止长时间停车";s:1:"B";s:18:"禁止停放车辆";s:1:"C";s:21:"仅允许装卸货物";s:1:"D";s:21:"仅允许上下人员";}', 'B', '路沿石上是黄色实线，表示不论临时还是长时，全部禁止停放车辆', '', 1, 101),
(120, 49, 1, 1, '图中圈内白色横实线是何含义？', '/Public/Uploads/2017-04-21/1492769013.jpg', 'a:4:{s:1:"A";s:9:"减速线";s:1:"B";s:9:"让行线";s:1:"C";s:9:"停止线";s:1:"D";s:9:"待转线";}', 'C', '这是车辆等红绿灯的时候以这条线为准停车，答案选停止线', '', 1, 101),
(121, 50, 1, 1, '路口最前端的双白实线是什么含义？', '/Public/Uploads/2017-04-21/1492769096.jpg', 'a:4:{s:1:"A";s:15:"左弯待转线";s:1:"B";s:15:"等候放行线";s:1:"C";s:15:"减速让行线";s:1:"D";s:15:"停车让行线";}', 'D', '图中有停字，表示要停车让行，答案选停车让行线。', '', 1, 101),
(123, 52, 1, 1, '这个路面标记是什么标线？', '/Public/Uploads/2017-04-21/1492776719.jpg', 'a:4:{s:1:"A";s:9:"网状线";s:1:"B";s:9:"导流线";s:1:"C";s:9:"禁驶区";s:1:"D";s:9:"中心圈";}', 'D', '这是用于交叉路口中心的标记，作用是区分左右大小转弯，车辆不得压线。答案是中心圈', '', 1, 101),
(124, 53, 1, 1, '这个路面标记是什么标线？', '/Public/Uploads/2017-04-21/1492776788.jpg', 'a:4:{s:1:"A";s:9:"网状线";s:1:"B";s:9:"导流线";s:1:"C";s:9:"中心圈";s:1:"D";s:9:"禁驶区";}', 'C', '这是用于交叉路口中心的标记，作用是区分左右大小转弯，车辆不得压线。答案是中心圈', '', 1, 101),
(125, 54, 1, 1, '图中路口中央黄色路面标记是什么标线？', '/Public/Uploads/2017-04-21/1492776861.jpg', 'a:4:{s:1:"A";s:9:"中心圈";s:1:"B";s:9:"导流线";s:1:"C";s:9:"停车区";s:1:"D";s:9:"网状线";}', 'D', '这是路口中心像网一样的标记，用于禁止车辆在路口内停车等候，答案是网状线。', '', 1, 101),
(126, 55, 1, 1, '图中圈内两条黄色虚线间的区域是何含义？', '/Public/Uploads/2017-04-21/1492776926.jpg', 'a:4:{s:1:"A";s:24:"营运客车专用车道";s:1:"B";s:21:"出租车专用车道";s:1:"C";s:18:"公交专用车道";s:1:"D";s:21:"大客车专用车道";}', 'C', '图中车道上有文字，公交专用车道。', '', 1, 101),
(127, 56, 1, 1, '道路最左侧白色虚线区域是何含义？', '/Public/Uploads/2017-04-21/1492776992.jpg', 'a:4:{s:1:"A";s:30:"未载客出租车专用车道";s:1:"B";s:24:"小型客车专用车道";s:1:"C";s:27:"多乘员车辆专用车道";s:1:"D";s:24:"大型客车专用车道";}', 'C', '图中车道有文字表示多乘员车辆专用车道。', '', 1, 101),
(128, 57, 1, 1, '路面上的白色标线是何含义？', '/Public/Uploads/2017-04-21/1492777068.jpg', 'a:4:{s:1:"A";s:27:"车行道纵向减速标线";s:1:"B";s:27:"车行道横向减速标线";s:1:"C";s:24:"车道变少提示标线";s:1:"D";s:24:"道路施工提示标线";}', 'B', '这种标线多用于下坡路段，在道路上是用横线规划的，提醒车辆应当减速慢行，答案是车行道横向减速标线。', '', 1, 101),
(129, 58, 1, 1, '路面上的菱形块虚线是何含义？', '/Public/Uploads/2017-04-21/1492777134.jpg', 'a:4:{s:1:"A";s:27:"车行道横向减速标线";s:1:"B";s:24:"车道变少提示标线";s:1:"C";s:24:"道路施工提示标线";s:1:"D";s:27:"车行道纵向减速标线";}', 'D', '这是一种和车道分界线平行的菱形块状虚线，对于驾驶人来说是竖着规划的，竖着的方向也就是纵向，答案选车行道纵向减速标线。', '', 1, 101),
(130, 59, 1, 1, '路面上的黄色标线是何含义？', '/Public/Uploads/2017-04-21/1492777200.jpg', 'a:4:{s:1:"A";s:21:"接近障碍物标线";s:1:"B";s:24:"路面宽度渐变标线";s:1:"C";s:21:"施工路段提示线";s:1:"D";s:21:"车行道变多标线";}', 'B', '我们可以看到图中左右两边路面宽度不一样，在红圈区域发生了变化，所以这就是路面宽度渐变标线。', '', 1, 101),
(131, 60, 1, 1, '路面上的黄色填充标线是何含义？', '/Public/Uploads/2017-04-21/1492777259.jpg', 'a:4:{s:1:"A";s:27:"接近移动障碍物标线";s:1:"B";s:24:"远离狭窄路面标线";s:1:"C";s:21:"接近障碍物标线";s:1:"D";s:24:"接近狭窄路面标线";}', 'C', '图中的黑色部分表示路面中间的固定性障碍物，黄色标线区域越来越宽，表示越来越接近障碍物了，答案选接近障碍物标线。', '', 1, 101),
(132, 61, 1, 1, '这种黄黑相间的倾斜线条是什么标记？', '/Public/Uploads/2017-04-21/1492777321.jpg', 'a:4:{s:1:"A";s:12:"减速标记";s:1:"B";s:12:"突起标记";s:1:"C";s:12:"立面标记";s:1:"D";s:12:"实体标记";}', 'C', '可以看到图中的斜线是竖立的，答案选立面标记。', '', 1, 101),
(133, 62, 3, 1, '路中心黄色虚线的含义是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '/Public/Uploads/2017-04-21/1492777385.jpg', NULL, 'A', '虚线可以越线，答对。', '', 1, 101),
(134, 63, 3, 1, '路中心的双黄实线作用是分隔对向交通流，在保证安全的前提下，可越线超车或转弯。', '/Public/Uploads/2017-04-21/1492777457.jpg', NULL, 'B', '实线不能越线，题目说可以越线，答错。', '', 1, 101),
(135, 64, 3, 1, '这个地面标记的含义是预告前方设有交叉路口。', '/Public/Uploads/2017-04-21/1492777514.jpg', NULL, 'B', '这个标记的含义是人行横道预告，题目说的是交叉路口，答错。', '', 1, 101),
(136, 65, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777595.jpg', 'a:4:{s:1:"A";s:12:"停止信号";s:1:"B";s:15:"左转弯信号";s:1:"C";s:18:"靠边停车信号";s:1:"D";s:15:"右转弯信号";}', 'A', '图中交警的手举过头顶，表示停止信号', '', 1, 101),
(137, 66, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777695.jpg', 'a:4:{s:1:"A";s:12:"转弯信号";s:1:"B";s:12:"直行信号";s:1:"C";s:18:"靠边停车信号";s:1:"D";s:12:"停止信号";}', 'B', '图中交警的两只手左右伸直，表示直行信号。', '', 1, 101),
(138, 67, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777769.jpg', 'a:4:{s:1:"A";s:15:"右转弯信号";s:1:"B";s:21:"左转弯待转信号";s:1:"C";s:15:"左转弯信号";s:1:"D";s:18:"减速慢行信号";}', 'B', '图中交警的左手移动幅度不大，表示让左转弯的车向前走一点，进入左转弯待转区，答案是左转弯待转信号。', '', 1, 101),
(139, 68, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777832.jpg', 'a:4:{s:1:"A";s:15:"右转弯信号";s:1:"B";s:18:"靠边停车信号";s:1:"C";s:12:"变道信号";s:1:"D";s:18:"减速慢行信号";}', 'C', '图中交警的手从左向右水平变动，表示变道信号', '', 1, 101),
(140, 69, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777896.jpg', 'a:4:{s:1:"A";s:18:"靠边停车信号";s:1:"B";s:12:"变道信号";s:1:"C";s:15:"右转弯信号";s:1:"D";s:18:"减速慢行信号";}', 'D', '图中交警的一只手从高到低，表示减速慢行，答案选减速慢行信号。', '', 1, 101),
(141, 70, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492777957.jpg', 'a:4:{s:1:"A";s:15:"右转弯信号";s:1:"B";s:21:"左转弯待转信号";s:1:"C";s:18:"靠边停车信号";s:1:"D";s:15:"左转弯信号";}', 'D', '图中交警右手平举，左手摆动指向我们的左边，表示左转弯信号', '', 1, 101),
(142, 71, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492778016.jpg', 'a:4:{s:1:"A";s:18:"靠边停车信号";s:1:"B";s:21:"左转弯待转信号";s:1:"C";s:18:"减速慢行信号";s:1:"D";s:15:"左转弯信号";}', 'D', '图中交警右手平举，左手摆动指向我们的左边，表示左转弯信号。', '', 1, 101),
(143, 72, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492778072.jpg', 'a:4:{s:1:"A";s:18:"靠边停车信号";s:1:"B";s:12:"停止信号";s:1:"C";s:18:"减速慢行信号";s:1:"D";s:15:"右转弯信号";}', 'D', '图中交警左手平举，右手摆动指向我们的右侧，表示右转弯信号。', '', 1, 101),
(144, 73, 1, 1, '这一组交通警察手势是什么信号？', '/Public/Uploads/2017-04-21/1492778133.jpg', 'a:4:{s:1:"A";s:15:"右转弯信号";s:1:"B";s:21:"左转弯待转信号";s:1:"C";s:18:"减速慢行信号";s:1:"D";s:18:"靠边停车信号";}', 'A', '图中交警左手平举，右手摆动指向我们的右边，答案是右转弯信号。', '', 1, 101),
(145, 74, 1, 1, '驾驶机动车在路口遇到这种情况如何行驶？', '/Public/Uploads/2017-04-21/1492778203.jpg', 'a:4:{s:1:"A";s:18:"可以向右转弯";s:1:"B";s:15:"靠右侧直行";s:1:"C";s:21:"遵守交通信号灯";s:1:"D";s:12:"停车等待";}', 'D', '图中交警一只手举过头顶，表示停止信号，我们要停车等待。', '', 1, 101),
(146, 1, 1, 1, '这属于哪一种标志？', '/Public/Uploads/2017-04-21/1492778383.jpg', 'a:4:{s:1:"A";s:12:"警告标志";s:1:"B";s:12:"指路标志";s:1:"C";s:12:"指示标志";s:1:"D";s:12:"禁令标志";}', 'D', '所有带红色圆圈的标志都属于禁令标志', '禁', 1, 102),
(147, 2, 1, 1, '禁令标志的作用是什么？', '', 'a:4:{s:1:"A";s:21:"禁止或限制行为";s:1:"B";s:18:"警告前方危险";s:1:"C";s:18:"告知方向信息";s:1:"D";s:18:"指示车辆行进";}', 'A', '禁令标志的作用就是禁止或限制行为。', '禁', 1, 102),
(148, 3, 1, 1, '这个标志是何含义？', '/Public/Uploads/2017-04-21/1492778786.jpg', 'a:4:{s:1:"A";s:12:"减速行驶";s:1:"B";s:12:"禁止驶入";s:1:"C";s:12:"限时进入";s:1:"D";s:12:"禁止通行";}', 'D', '红色圆圈是实线，没有开门，不能进也不能出，所以答案选禁止通行', '通', 1, 102),
