(2313, 97, 3, 1, '前方标志指示高速公路的名称和编号。', '/Public/Uploads/2017-04-24/1493046223.png', NULL, 'A', '右侧标志表示高速公路命名编号，答对', '名称和编号', 4, 208),
(2314, 98, 3, 1, '右侧这个标志提醒注意前方200米是堤坝道路。', '/Public/Uploads/2017-04-24/1493046268.png', NULL, 'A', '右侧主标志表示前方是堤坝路段，副标志表明距离为200米，答对。', '200', 4, 208),
(2315, 99, 3, 1, '右侧标志提醒前方200米有村庄。', '/Public/Uploads/2017-04-24/1493046307.png', NULL, 'A', '右侧主标志表示前方有村庄，副标志表明距离为200米，答对', '200', 4, 208),
